import React, {useState}  from "react";
import styled from "styled-components";
import Button from "../components/Button";

function CodingExercise(props) {
  const [value,setValue] = useState('');
  return (
    <Container>
      <Rect2>
        <InsertValue>Insert Value : 
          <input type="text" value={value} style={{width: 100,
                                  padding: 10,
                                  margin: 8,
                                  border: 1}} />
          </InsertValue>

        <Button
          style={{
            height: 38,
            width: 97,
            backgroundColor: "rgba(95,75,139,1)",
            marginTop: 15,
            marginLeft: 400
          }}
        ></Button>
      </Rect2>
      <br></br>
      <table id="simple-board">
        <tbody>
          <tr id="row0">
            <td id="cell0-0"></td>
            <td id="cell0-1"></td>
            <td id="cell0-2"></td>
          </tr>
          <tr id="row1">
            <td id="cell1-0"></td>
            <td id="cell1-1"></td>
            <td id="cell1-2"></td>
          </tr>
          <tr id="row2">
            <td id="cell2-0"></td>
            <td id="cell2-1"></td>
            <td id="cell2-2"></td>
          </tr>
        </tbody>
      </table>
      <Paragraph>
        Write an application that given a number of pennies will calculate the
        minimum number of Sterling coins equivalent to that amount.{"\n"}Eg.
        123p = 1 x Â£1, 1 x 20p, 1 x 2p, 1 x 1p
      </Paragraph>
      <ProblemStatement>Problem Statement :</ProblemStatement>
      <Title>Coding Exercise :</Title>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  background-color: rgba(95,75,139,1);
  flex-direction: column;
  height: 100vh;
  width: 100vw;
`;

const Rect2 = styled.div`
  width: 572px;
  height: 179px;
  background-color: rgba(227,159,141,1);
  flex-direction: column;
  display: flex;
  margin-top: 289px;
  margin-left: 44px;
`;

const InsertValue = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 700;
  color: rgba(95,75,139,1);
  font-size: 18px;
  margin-top: 46px;
  margin-left: 14px;
`;

const Paragraph = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(227,159,141,1);
  font-size: 20px;
  margin-top: -272px;
  margin-left: 44px;
`;

const ProblemStatement = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(227,159,141,1);
  font-size: 31px;
  margin-top: -107px;
  margin-left: 44px;
`;

const Title = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(227,159,141,1);
  font-size: 45px;
  margin-top: -116px;
  margin-left: 44px;
`;

const Rect3 = styled.div`
  width: 411px;
  height: 390px;
  background-color: rgba(227,159,141,1);
  margin-left: 254px;
`;

export default CodingExercise;
