import React, {useState, Component}  from "react";
import styled from "styled-components";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';


class CodingExercise extends React.Component {

    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.value);
      this.state.value = '75'
      var val = this.state.value;
      var arr = [200,100,50,20,10,5,1];

      for (var i=0;i<arr.length;i++)
      {
        var temp = parseInt(val/arr[i]);
        if(temp!="0")
        {
          console.log(arr[i] +" "+ temp);
          val = val - parseInt(val%arr[i]);
        }
      }
    }
    render() {
  return (

    <Container>
      {parseInt(5/5)}
      <Title>Coding Exercise :</Title>
      <ProblemStatement>Problem Statement :</ProblemStatement>
      <Paragraph>
        Write an application that given a number of pennies will calculate the
        minimum number of Sterling coins equivalent to that amount.{"\n"}Eg.
        123p = 1 x £1, 1 x 20p, 1 x 2p, 1 x 1p
      </Paragraph>

      <form onSubmit={this.handleSubmit}>
      <Rect2>
        <label style={{ marginTop:"5%"}}>
          <InsertValue>Insert Value:</InsertValue>
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="button" value="Submit" onClick={() => this.handleSubmit()} style={{marginTop:"5%", width:"15%", marginLeft:"60%"}}/>
        </Rect2>
      </form>

      <TableContainer >
      <Table style={{width: '50%',backgroundColor:'rgba(95,75,139,1)',color: 'rgba(95,75,139,1)',marginLeft: '44px'}} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Sterling coins</TableCell>
            <TableCell align="right">Minimum number of Sterling coins</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
            </TableRow>
          ))}

        </TableBody>
      </Table>
    </TableContainer>
    </Container>
  );
}
}

function createData(name, calories) {
  return { name, calories };
}

const rows = [
  createData('2', 159),
  createData('1', 237),
  createData('50p', 305),
  createData('20p', 262),
  createData('10p', 356),
  createData('5p', 356),
  createData('2p', 356),
  createData('1p', 356),
];


const Container = styled.div`
  display: flex;
  background-color: rgba(95,75,139,1);
  flex-direction: column;
  height: 100%;
  width: 100vw;
`;

const Rect2 = styled.div`
  width: 572px;
  height: 179px;
  background-color: rgba(227,159,141,1);
  flex-direction: column;
  display: flex;
  margin-top: 50px;
  margin-left: 44px;
`;

const InsertValue = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 700;
  color: rgba(95,75,139,1);
  font-size: 18px;
  margin-top: 46px;
  margin-left: 14px;
`;

const Paragraph = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(227,159,141,1);
  font-size: 20px;
  margin-top: 20px;
  margin-left: 44px;
`;

const ProblemStatement = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(227,159,141,1);
  font-size: 31px;
  margin-top: 20px;
  margin-left: 44px;
`;

const Title = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(227,159,141,1);
  font-size: 45px;
  margin-top: 10px;
  margin-left: 44px;
`;

const Rect3 = styled.div`
  width: 411px;
  height: 390px;
  background-color: rgba(227,159,141,1);
  margin-left: 254px;
`;

export default CodingExercise;
